import classes from './RoomList.css';
import firebase from '../../firebase'
import { useState, useEffect } from 'react'
import { Image } from 'react'

const RoomList = () => {

  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);

  const ref = firebase.firestore().collection("rooms");

  function deleteRoom(room){
    ref.doc(room.rid).delete();
  }

  function createRoom(){
    return(
      <h2>
        <input type="text" placeholder="rid"></input>
        <input type="text" placeholder="title"></input>
        <input type="text" placeholder="description"></input>
        <input type="text" placeholder="capacity"></input>
        <input type="text" placeholder="price"></input>
        <input type="text" placeholder="photosIds"></input>
      </h2>
    )
  }

  function getRooms() {
    setLoading(true);
    ref.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setRooms(items);
      setLoading(false);
    })
  }

  useEffect(() => {
    getRooms();
  },[]);

  if (loading){
    return <h1>Loading</h1>;
  }

  return (
    <section className={classes.rooms}>
      <h1>Room List</h1>
      <button></button>
      {rooms.map((room) => (
        <div key={room.rid}>
          <h2>
            <h3>{room.title}</h3>
            <h4>Description: {room.description}</h4>
            <h4>Capacity: {room.capacity}</h4>
            <h4>Price: {room.price}</h4>
            {/* <h4><Image style={{width: '70%', height: '70%'}} source={{uri: room.photosIds}} /></h4> */}
            <button onClick={() => deleteRoom(room)}>Delete</button>
          </h2>
        </div>
      ))}
    </section>

  );
};

export default RoomList;
