import classes from './RoomList.css';
import firebase from '../../firebase'
import { useState, useEffect } from 'react'
import { Image } from 'react'

function createRoom(){

  const [rid, setRid] = useState();
  const [title, setTitle] = useState();
  const [description, setDescription] = useState();
  const [capacity, setCapacity] = useState();
  const [price, setPrice] = useState();
  const [photosIds, setPhotosIds] = useState();

  return(
    <h2>
      <input type="text" id="rid1" placeholder="rid" onChange={(e) => setRid(e.target.value)}></input>
      <input type="text" id="title1" placeholder="title" onChange={(e) => setTitle(e.target.value)}></input>
      <input type="text" id="description1" placeholder="description" onChange={(e) => setDescription(e.target.value)}></input>
      <input type="text" id="capacity1" placeholder="capacity" onChange={(e) => setCapacity(e.target.value)}></input>
      <input type="text" id="price1" placeholder="price" onChange={(e) => setPrice(e.target.value)}></input>
      <input type="text" id="photosIds1" placeholder="photosIds" onChange={(e) => setPhotosIds(e.target.value)}></input>
      <button onClick={() => {
        document.getElementById("rid1").value = ""
        document.getElementById("title1").value = ""
        document.getElementById("description1").value = ""
        document.getElementById("capacity1").value = ""
        document.getElementById("price1").value = ""
        document.getElementById("photosIds1").value = ""
      }}>Create Room</button>
    </h2>
  )
}

const RoomList = () => {

  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);

  const ref = firebase.firestore().collection("rooms");

  function deleteRoom(room){
    ref.doc(room.rid).delete();
  }

  function getRooms() {
    setLoading(true);
    ref.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setRooms(items);
      setLoading(false);
    })
  }

  useEffect(() => {
    getRooms();
  },[]);

  if (loading){
    return <h1>Loading</h1>;
  }

  return (
    <section className={classes.rooms}>
      <h1>Room List</h1>
      <button></button>
      {rooms.map((room) => (
        <div key={room.rid}>
          <h2>
            <h3>{room.title}</h3>
            <h4>Description: {room.description}</h4>
            <h4>Capacity: {room.capacity}</h4>
            <h4>Price: {room.price}</h4>
            {/* <h4><Image style={{width: '70%', height: '70%'}} source={{uri: room.photosIds}} /></h4> */}
            <button onClick={() => deleteRoom(room)}>Delete</button>
          </h2>
        </div>
      ))}
    </section>

  );
};

export default RoomList;
