import classes from './RoomList.css';
import firebase from '../../firebase'
import { useState, useEffect } from 'react'
import { Image } from 'react'

const RoomList = () => {

  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [url, setUrl] = useState();

  useEffect(() => {
    const func = async () => {
      const storage = firebase.storage;
    }
  })

  const ref = firebase.firestore().collection("rooms");

  function getRooms() {
    setLoading(true);
    ref.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setRooms(items);
      setLoading(false);
    })
  }

  useEffect(() => {
    getRooms();
  },[]);

  if (loading){
    return <h1>Loading</h1>;
  }

  return (
    <section className={classes.rooms}>
      <h1>Room List</h1>
      {rooms.map((room) => (
        <div key={room.rid}>
          <h2>
            <h3>{room.title}</h3>
            <h4>Description: {room.description}</h4>
            <h4>Capacity: {room.capacity}</h4>
            <h4>Price: {room.price}</h4>
            <h4><Image source={{uri: room.photosIds}} /></h4>
          </h2>
        </div>
      ))}
    </section>

  );
};

export default RoomList;
