import classes from './RoomList.css';
import firebase from '../../firebase'
import { useState, useEffect } from 'react'
import { Image, StyleSheet } from 'react'

const styles=StyleSheet.create({
  body:{
    padding: 25,
    margin:25,
    backgroundColor:'orange',
    flex: 1
  },
  stretch:{width:50,height:50,
    resizeMode:'stretch',},
    row:{
    backgroundColor:'#fff',
    
    borderBottomWidth:4,
    }
  })

const RoomList = () => {

  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);

  const ref = firebase.firestore().collection("rooms");

  function getRooms() {
    setLoading(true);
    ref.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setRooms(items);
      setLoading(false);
    })
  }

  useEffect(() => {
    getRooms();
  },[]);

  if (loading){
    return <h1>Loading</h1>;
  }

  return (
    <section className={classes.rooms}>
      <h1>Room List</h1>
      {rooms.map((room) => (
        <div key={room.rid}>
          <h2>
            <h3>{room.title}</h3>
            <h4>Description: {room.description}</h4>
            <h4>Capacity: {room.capacity}</h4>
            <h4>Price: {room.price}</h4>
            <h4><Image style={styles.stretch} source={{uri: 'room.photosIds'}} /></h4>
          </h2>
        </div>
      ))}
    </section>

  );
};

export default RoomList;
