import classes from './Photos.module.css';
import { storage } from "../../firebase"
import {ref, uploadBytes} from "firebase/storage"
import { useState, useEffect } from 'react'
import { v4 } from "uuid"

const Photos = () => {

    const [image, setImage]= useState(null);
    const uploadImage = () => {
        if(image == null) return;
        const imageRef = ref(storage, `${image.name + v4()}`)
        uploadBytes(imageRef, image).then(() => {
            alert("image uploaded");
        })

    }

    const [imageTab, setImageTab] = useState([]);

  useEffect(() => {
    storage()
      .ref('images/')
      .listAll()
      .then(function(result) {
          result.items.forEach(function(imageRef) {
              imageRef.getDownloadURL().then(function(url) {
                  imageTab.push(url);
                  setImageTab(imageTab);
              }).catch(function(error) {
              });
          });
      })
      .catch((e) => console.log('Errors while downloading => ', e));
  }, []);
      
    return (
        <header>
            <h1 className={classes.title}>Photos List</h1>
            {imageTab.map(i => (<Image style={{height: 200, width: 200}} source={{uri: i}} />))}
            <section className={classes.auth}>
                <div><input type="file" onChange={(e) => {setImage(e.target.files[0])}}/></div>
                <div><button onClick={uploadImage}> Upload Photo </button></div>
            </section>
        </header>

    
      );

}
export default Photos;
