import classes from './BookingList.module.css';
import firebase from '../../firebase'
import { useState, useEffect } from 'react'

const ref = firebase.firestore().collection("requests");

const BookingList = () => {

  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(false);

    function changeStatus(request, statusUpdate){
        ref.doc(request.uid).update({status: statusUpdate,}).then(() => {
            console.log('User updated!');
          });
    }

  function getRequests() {
    setLoading(true);
    ref.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setRequests(items);
      setLoading(false);
    })
  }

  useEffect(() => {
    getRequests();
  }, []);

  if (loading){
    return <h1>Loading</h1>;
  }

  return (
    <header>
      <h1 className={classes.title}>Request List</h1>
      {requests.map((request) => (
        <div key={request.uid}>
          <section className={classes.auth}>
            <div>Camera Id: {request.rid}</div>
            <div>UID: {request.uid}</div>
            <div>Start Date: {request.startDate}</div>
            <div>End Date: {request.endDate}</div>
            <div>Status: {request.status}</div>
          </section>
        </div>
      ))}
    </header>

  );
};

export {ref};
export default BookingList;
